import React from 'react';
import axios from 'axios';
//import response from 'response';
class EditListing extends React.Component {


    constructor(props) {
        super(props);

        this.state = {
            Title: '',
            Year: '',
            Poster: ''
        };

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleGameTitleChange = this.handleGameTitleChange.bind(this);
        this.handleGameYearChange = this.handleGameYearChange.bind(this);
        this.handleGamePosterChange = this.handleGamePosterChange.bind(this);
    }


    componentDidMount() {
        alert(this.props.match.params.id)

        axios.get('http://localhost:4000/api/games/' + this.props.match.params.id)

          .then((response) => {
            this.setState({
                Title: response.data.title,
                Year: response.data.year,
                Poster: response.data.poster,
                _id: response.data._id
            })
          })
        .catch();
    }

    handleGameTitleChange(e) {
        this.setState({ Title: e.target.value });
    }

    handleGameYearChange(e) {
        this.setState({ Year: e.target.value });
    }

    handleGamePosterChange(e) {
        this.setState({ Poster: e.target.value });
    }

    handleSubmit(e) {
        alert(this.state.Title + "      " + this.state.Year
            + "       " + this.state.Poster);
        e.preventDefault();


        const newGane = {
            title: this.state.Title,
            year: this.state.Year,
            poster: this.state.Poster
        };
        this.setState({
            Title: '',
            Year: '',
            Poster: ''
        });

        axios.put('http://localhost:4000/api/Game/' + this.state._id)
            .then()
            .catch();
    }
    render() {
        return (
            <div>
                <h1>Edit Game details</h1>
                <form onSubmit={this.handleSubmit}>
                    <div className='form-group'>
                        <label>Game Title</label>
                        <input
                            type='text'
                            className='form-control'
                            value={this.state.Title}
                            onChange={this.handleGameTitleChange}
                        ></input>
                    </div>
                    <div className='form-group'>
                        <label>Game Year</label>
                        <input
                            type='text'
                            className='form-control'
                            value={this.state.Year}
                            onChange={this.handleGameYearChange}
                        ></input>
                    </div>
                    <div className='form-group'>
                        <label>Movie Game Url</label>
                        <textarea
                            row='3'
                            className='form-control'
                            value={this.state.Poster}
                            onChange={this.handleGamePosterChange}
                        ></textarea>
                    </div>
                    <div>
                        <input
                            type="submit"
                            value="Edit Game">
                        </input>
                    </div>
                </form>
            </div>
        )
    }
}

export default EditListing;
